insert into gebruiker (gebruiker_id, gebruiker_naam, gebruiker_wachtwoord, gebruiker_admin) values (1, 'admin', 'admin', 1);
insert into casus (casus_id, casus_omschrijving, casus_definitie, casus_begindatum, casus_einddatum) values (12, 'Opname van een meneer met decompensatio cordis', 'Meneer de Boer (60 jaar) is vandaag opnieuw opgenomen op de afdeling Cardiologie. Hij is bekend met chronisch hartfalen als gevolg van een groot voorwond infarct 5 jaar geleden. Het is de vijfde ziekenhuisopname op rij dit jaar voor decompensatie/astma cardiale. In de laatste 2 weken voor deze opname is meneer de Boer 5 kilo aangekomen in gewicht, tegelijkertijd is hij toenemend kortademig, op dit moment ook in rust. Hij slaapt slecht, omdat hij niet plat kan liggen, vaak moet plassen ''s nachts en regelmatig last heeft van kriebelhoest. Hij is daardoor erg moe en niet meer in staat om zijn dagelijkse dingen, zoals bv de wandeling met de hond, te doen.\r\n\r\nMevrouw de Boer heeft vanochtend gebeld met de HF verpleegkundige, omdat haar man opnieuw de hele nacht niet had geslapen en extreem benauwd was. De hartfalen verpleegkundige adviseerde mevrouw de Boer naar de spoedpoli te komen. Op de spoedpoli is diagnostiek verricht, de diagnose decompensatio cordis wordt vastgesteld en diuretica iv gestart. Vanaf de spoedpoli is dhr opgenomen op de afdeling Cardiologie.\r\n\r\nIn gesprek met meneer de Boer vraagt de verpleegkundige hoe het is gegaan in de afgelopen periode. Meneer de Boer vertelt dat het na de vorige opname eerst wel weer goed ging, maar dat hij langzamerhand toch weer benauwder en vermoeider werd. Hij zegt wel te weten wat hartfalen is en waar hij op moet letten; “ik wist wel dat ik was aangekomen en dat ik moet bellen als ik meer dan 2 kilo ben aangekomen in 3 dagen, maar ik dacht ik heb toch volgende week een afspraak op de poli, dat kan dan wel. Op de vraag of hij zijn medicijnen goed inneemt antwordt meneer de Boer; jawel hoor, ik neem ze meestal wel, alleen als ik niet veel klachten heb neem ik geen plaspillen, dan denk ik bij mezelf “Gerrit, je hebt een goede dag vandaag, geniet er van!” Ik probeer zo gezond mogelijk te lven en die chemische troep zoveel mogelijk te laten staan”.\r\n\r\nLichamelijk onderzoek\r\nRR					145/90 mmHG\r\nT					36.5 C\r\nPols				84/minuut\r\nAdemhaling			2/minuut\r\nEnkeloedemen		++\r\nLengte				185 cm\r\nGewicht				102 kg\r\n\r\nMedicatie	\r\nPerindopril 4mg		1xdd\r\nBisoprolol 10mg		1xdd\r\nSpironolacton 25mg	1xdd\r\nOmeprazol 40mg		1xdd\r\nAnenoucomarol		zn\r\nMetfamine 500mg	2xdd\r\n', '2015-06-15 10:02:16', NULL);